# coding:utf-8import pandas as pdimport numpy as npimport osimport openpyxlfrom datetime import datetime,date,timedelta#自定义printdef printInfo(str,obj):    print ('\n'+'-'*15+str+'-'*15+'\n')    print (obj)#初始化处理数据def initData(date_str):    PROJECT_ROOT = os.path.dirname(os.path.realpath(__file__))    path = os.path.join(PROJECT_ROOT, 'data/企业报名转化_' + date_str + '.xlsx')    df = pd.read_excel(path)    # 去掉第一行的数字columns    df = df.rename(columns=df.loc[0])    # 删除多余的第一行数据    df.drop(labels=0, axis=0, inplace=True)    # 重新排序设置index    df = df.reset_index(drop=True)    printInfo('显示数据源head10',df.head(10))    printInfo('元数据shape',df.shape)    print(df.info())    # 获取公司名称为Nan的数据的index    index = df[df['已申请的公司'].isnull().values == True].index    # 删除对应公司数据    df = df.drop(index)    # 按照申请公司和领取用户数倒叙排列，然后删除重复项，保留第一个，等同于保留最大值    df = df.sort_values(by=['已申请的公司', '领取用户数'], ascending=False).drop_duplicates(subset='已申请的公司', keep='first',                                                                                      inplace=False).reset_index(        drop=True)    printInfo('排序之后去重数据', df)    printInfo('排序去重后领课用户数和观看课程用户数', df[['领取用户数', '观看课程用户数']].sum())    df['领取用户数'] = df['领取用户数'].astype(int)    df['观看课程用户数'] = df['观看课程用户数'].astype(int)    df['观看转化率'] = df['观看转化率'].astype(float)    df['平均学习时长（秒）'] = df['平均学习时长（秒）'].astype(float)    return df#赋值函数def set_course_count_value(result_df,data,index,top_number):    result_df.loc[index,'领取用户数'] = data['领取用户数'][0:top_number].sum()    result_df.loc[index,'观看课程用户数'] = data['观看课程用户数'][0:top_number].sum()    result_df.loc[index,'领取占比整体'] = data['领取用户数'][0:top_number].sum() / data['领取用户数'].sum()    result_df.loc[index,'观看占比整体'] = data['观看课程用户数'][0:top_number].sum() / data['观看课程用户数'].sum()    return result_df#头部TOP10企业领取，看课数量，占比整体百分比，分社归属分布def get_course_count(df):    data = df[['归属分社','已申请的公司', '申请人', '领取用户数', '观看课程用户数']].sort_values(by='领取用户数', ascending=False).reset_index(drop=True)    result_df = pd.DataFrame(index=['TOP1','TOP前10'],columns=['领取用户数','观看课程用户数','领取占比整体','观看占比整体'])    printInfo('数据输出：前10名企业领取情况',data.head(10))    #获取头部用户占比情况    set_course_count_value(result_df,data,'TOP1',1)    set_course_count_value(result_df,data,'TOP前10',10)    printInfo('数据输出：头部用户占比',result_df)    return result_df#2.每日新增领取公司倒序排列（数量和人数与当日新增匹配）def day_increase_data(df):    printInfo('day_increase_data','')#1.平均值：领取用户，看课，观看转化率，学习观看时长，企业平均看课人数def community_mean_data(df):    grouped = df.groupby(by='归属分社')[['领取用户数', '观看课程用户数', '观看转化率', '平均学习时长（秒）']].agg({        '领取用户数': np.sum,        '观看课程用户数': np.sum,        '观看转化率': np.mean,        '平均学习时长（秒）': np.mean    }).sort_values(by='领取用户数', ascending=False)    printInfo('分社维度',grouped)    #插入'归属分社'一列    grouped.insert(0, '归属分社', grouped.index)    #重置index    result_df = grouped.reset_index(drop=True)    #过滤掉领取数为0的分社，多数为虚拟分社    result_df = result_df[result_df['领取用户数']>0]    printInfo ('数据输出：不同分社基础数据',result_df)    return result_df'''整体：1.头部TOP10企业领取，看课数量，占比整体百分比，分社归属分布2.每日新增领取公司倒序排列（数量和人数与当日新增匹配）分社维度划分，1.平均值：领取用户，看课，观看转化率，学习观看时长，企业平均看课人数2.每个分社头部3个企业：分别是谁，领取用户，看课人数 占比整体情况3.企业领取用户数区间分布现有已建立上联系企业的信息分布情况'''def init():    yesterday = (date.today() + timedelta(days=-1)).strftime("%Y-%m-%d")  # 昨天日期    last_last_day =  (date.today() + timedelta(days=-2)).strftime("%Y-%m-%d") #前天日期    printInfo('日期',last_last_day)    df = initData(yesterday)    get_course_count(df)    community_mean_data(df)    #计算累计新增公司    day_increase_data(df)init()