# coding:utf-8import pandas as pdimport numpy as npimport osimport matplotlib.pyplot as pltimport matplotlibfrom matplotlib.font_manager import FontPropertiesfrom matplotlib.ticker import MultipleLocator#画围观人数分布曲线def drawNumPic(num_df,time_interval_df):    #创建画板    plt.figure(figsize=(num_df['日期'].shape[0] / 1.2,15),dpi=80)    #划分第一个区间    plt.subplot(2,1,1)    x = np.array(num_df['日期'])    title_array = np.array(['总围观人数','社员围观人数','老注册围观人数','新注册围观人数'])    color_array = np.array(['b','r','g','c'])    for i in range(len(title_array)):        columns_str = title_array[i]        plt.plot(x,num_df[columns_str],color=color_array[i],label=columns_str)    plt.title('围观人数')    #设置y的范围    plt.yticks(np.arange(0,8000,1000))    #设置x坐标旋转角度    plt.xticks(rotation=45)    #设置图例    plt.legend()    #网格线    plt.grid(axis='y')    for i in range(len(x)):        x_value = x[i]        for title in title_array:            y_value = num_df[title][i]            plt.text(x_value,y_value+5,y_value,ha='center',va='bottom',fontsize=14)    #构建围观人数百分比分布（柱状图）    plt.subplot(2,2,3)    title_array = np.array(['社员围观人数','老注册围观人数','新注册围观人数'])    rate_df = pd.DataFrame(columns=title_array)    rate_df['社员围观人数'] = num_df['社员围观人数'] / num_df['总围观人数']    rate_df['老注册围观人数'] = num_df['老注册围观人数'] / num_df['总围观人数']    rate_df['新注册围观人数'] = num_df['新注册围观人数'] / num_df['总围观人数']    plt.bar(x,rate_df['社员围观人数'],width=0.5,color='b',label='社员围观人数')    plt.bar(x,rate_df['老注册围观人数'],width=0.5,color='r',label='老注册围观人数',bottom=rate_df['社员围观人数'])    plt.bar(x,rate_df['新注册围观人数'],width=0.5,color='g',label='新注册围观人数',bottom=rate_df['老注册围观人数']+rate_df['社员围观人数'])    # for i in range(len(x)):    #     x_value = x[i]    #     y1_value = rate_df['社员围观人数'][i]    #     y2_value = rate_df['老注册围观人数'][i]    #     y3_value = rate_df['新注册围观人数'][i]    #     y1_num = num_df['社员围观人数'][i]    #     y2_num = num_df['老注册围观人数'][i]    #     y3_num = num_df['新注册围观人数'][i]    #     plt.text(x_value, y1_value / 2, y1_num, ha='center', va='bottom', fontsize=14)    #     plt.text(x_value, y1_value + y2_value / 2, y2_num, ha='center', va='bottom', fontsize=14)    #     plt.text(x_value, y1_value + y2_value + y3_value / 2, y3_num, ha='center', va='bottom', fontsize=14)    # for i in range(len(x)):    #     x_value = x[i]    #     #用来计算y坐标的    #     temp = 0    #     for j in range(len(title_array)):    #         title = title_array[j]    #         y_value = rate_df[title][i]    #         y_num = num_df[title][i]    #         plt.text(x_value, temp + y_value / 2, y_num, ha='center', va='bottom', fontsize=14)    #         temp = temp + y_value    plt.grid(axis='y')    plt.title('围观人数分布')    plt.xticks(rotation=45)    plt.legend()    #30分钟和60分钟以上曲线    plt.subplot(2,2,4)    plt.plot(x,time_interval_df['30分钟以上'],color='b',label='30分钟以上')    plt.plot(x,time_interval_df['60分钟以上'],color='r',label='60分钟以上')    plt.xticks(rotation=45)    plt.grid(axis='y')    plt.title('30分钟以上和60分钟以上')    plt.legend()    for i in range(len(x)):        x_value = x[i]        y1_value = time_interval_df['30分钟以上'][i]        y2_value = time_interval_df['60分钟以上'][i]        plt.text(x_value,y1_value+0.5,y1_value,ha='center',va='bottom',fontsize=14)        plt.text(x_value,y2_value+0.5,y2_value,ha='center',va='bottom',fontsize=14)    #保存图片    path = '/Users/fujinshi/Desktop/多人讨论-区分付费/围观人数.png'    plt.savefig(path,dpi=150,bbox_inches='tight')    plt.show()def initData():    # 让中文显示正常    plt.rcParams['font.sans-serif'] = ['Microsoft YaHei']  # 用来正常显示中文标签字体。Microsoft YaHei 或 SimHei    plt.rcParams['axes.unicode_minus'] = False  # 用来正常显示负号    path = '/Users/fujinshi/Desktop/多人讨论-区分付费/58天区分付费统计结果.xlsx'    num_df = pd.read_excel(path,sheet_name='主题维度分人群数据')    time_interval_df = pd.read_excel(path,sheet_name='平均时长分布')    join_count_df = pd.read_excel(path,sheet_name='围观用户天数分布')    print (num_df.head(10))    print (time_interval_df.head(10))    print 